!(function () {
  if (
    ($(".flatpickr").length &&
      flatpickr(".flatpickr", {
        disableMobile: !0,
        minDate: "today",
        monthSelectorType: "static",
      }),
    $("#showGallery").length &&
      $("#showGallery").on("click", function () {
        $(".gallery").next().magnificPopup("open");
      }),
    $("#myCarousel").length)
  ) {
    new bootstrap.Carousel("#myCarousel");
  }
  if ($(".needs-validation").length) {
    const a = document.querySelectorAll(".needs-validation");
    Array.from(a).forEach((a) => {
      a.addEventListener(
        "submit",
        (e) => {
          a.checkValidity() || (e.preventDefault(), e.stopPropagation()),
            a.classList.add("was-validated");
        },
        !1
      );
    });
  }
})(),
  $(window).on("scroll", function () {
    $(window).scrollTop() + 0 > 0
      ? $(".navbar-transparent").addClass(
          "navbar-fixed animate__animated animate__fadeInDown"
        )
      : $(".navbar-transparent").removeClass(
          "navbar-fixed animate__animated animate__fadeInDown"
        );
  });

  // custom cursor

  const cursor = document.querySelector(".dot");
  const cursorOutline = document.querySelector(".cursor-outline");
  
  window.addEventListener("mousemove", (e) => {
    const x = e.clientX;
    const y = e.clientY;
  
    cursor.style.left = x + "px";
    cursor.style.top = y + "px";
  
 //    cursorOutline.style.left = x + "px";
 //    cursorOutline.style.top = y + "px";
 cursorOutline.animate({
     left: x + "px",
     top: y + "px",
      }, { duration: 500, fill: "forwards" });
 
  });
  
  const buttons = document.querySelectorAll(".hover-button");
 
 buttons.forEach((btn) => {
   btn.addEventListener("mouseenter", () => {
     cursorOutline.classList.add("scale-up");
   });
 
   btn.addEventListener("mouseleave", () => {
     cursorOutline.classList.remove("scale-up");
   });
 });


 

